CREATE PROCEDURE [dtx].[aw_GetServerVLFCount]
 @Help TINYINT = 0,
 @DataCollectionRound INT = 1,
 @ExportToTable TINYINT = 0,
 @ExportColumns TINYINT = 0,
 @ExportColumnNames NVARCHAR(2048) = '',
 @ExportDbName NVARCHAR(255) = N'',
