--	[DataCollectionRound] [int] NULL,
--	[QueryDate] [datetime] NOT NULL,
--	[MachineName] [sql_variant] NOT NULL,
--	[ServerName] [sql_variant] NOT NULL,
--	[Instance] [sql_variant] NULL,
-- .
-- .
-- .
